import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState } from 'react';
import { Jumbotron, Container, Button } from 'reactstrap';
import Head from 'next/head';
import { library } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { fas } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import Menu from '../components/Menu';

library.add(fas);

const Home = (data) => (
    <div>
        <Head>
            <title>Home Page</title>
            <meta name="description" content="Site de ... sobre ..." />
            <meta name="author" content="Leo Buck" />
            <link rel="shortcut icon" href="/favicon.ico" />
        </Head>
        <Menu />
        <Jumbotron fluid className="descr-top">
            <style>{`.descr-top {
                background-color: #000;
                color: #FFF;
                padding-top: 150px;
                padding-bottom: 100px;
                margin-bottom: 0rem !important;
                position: relative;
            }
            .inicio-link {
                position: absolute;
                top: 0px;
            }`}</style>
            <Container className="text-center">
                <a name="inicio" className="inicio-link"></a>
                <h1 className="display-4 mb-4">{data.response.home.topTitulo}</h1>
                <p className="lead mb-4">{data.response.home.topSubtitulo}</p>
                
                <Button outline color="warning" size="lg">{data.response.home.topTextButton}</Button>
            </Container>
        </Jumbotron>
        <Jumbotron fluid className="servicos">
            <style>{`.servicos {
                background-color: #FFF;
                padding-top: 80px;
                padding-bottom: 80px;
                margin-bottom: 0rem !important;
                position: relative;
            }
            .servicos-link {
                position: absolute;
                top: 0px;
            }
            .circulo {
                width: 140px;
